#Convert ASCII text to binary digits
def charToBin(text: str) -> list[int]:
    bitsPerCharacter = 8
    bitsTX = ''

    for char in text:
        info = bin(int.from_bytes(char.encode(),"big"))
        info = info[2:]

        if len(info) <= bitsPerCharacter:
            for i in range(0,bitsPerCharacter-len(info)):
                info = '0'+ info
            bitsTX += info
    bitsTX = list(map(int,[*bitsTX]))
    return bitsTX

